import React, { useState, useEffect } from "react";

export default function StudentForm({ onCreate, onUpdate, editing }) {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");

  useEffect(() => {
    if (editing) {
      setName(editing.name);
      setAge(editing.age);
    } else {
      setName("");
      setAge("");
    }
  }, [editing]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = { name, age };
    if (editing) {
      onUpdate(editing.id, data);
    } else {
      onCreate(data);
    }
    setName("");
    setAge("");
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "15px",
        alignItems: "center"
      }}
    >
      {/* Input Nombre */}
      <input
        type="text"
        placeholder="Nombre del estudiante"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{
          width: "100%",
          maxWidth: "300px",
          padding: "14px 18px",
          fontSize: "1em",
          borderRadius: "12px",
          border: "2px solid #00ff88",
          backgroundColor: "rgba(0,0,0,0.7)",
          color: "#e0e0e0",
          boxShadow: "0 0 8px #00ff88 inset"
        }}
      />

      {/* Input Edad */}
      <input
        type="number"
        placeholder="Edad"
        value={age}
        onChange={(e) => setAge(e.target.value)}
        style={{
          width: "100%",
          maxWidth: "300px",
          padding: "14px 18px",
          fontSize: "1em",
          borderRadius: "12px",
          border: "2px solid #00ff88",
          backgroundColor: "rgba(0,0,0,0.7)",
          color: "#e0e0e0",
          boxShadow: "0 0 8px #00ff88 inset"
        }}
      />

      {/* Botón Guardar/Actualizar */}
      <button
        type="submit"
        style={{
          width: "40%",
          maxWidth: "200px",
          padding: "12px 0",
          fontSize: "1em",
          borderRadius: "12px",
          fontWeight: "bold",
          cursor: "pointer",
          background: "linear-gradient(45deg, #00ff88, #00ffaa)",
          color: "#000",
          boxShadow: "0 0 25px #00ffaa, 0 0 50px #00ff88",
          transition: "0.3s"
        }}
        onMouseEnter={(e) =>
          (e.target.style.boxShadow = "0 0 50px #00ffaa, 0 0 100px #00ff88")
        }
        onMouseLeave={(e) =>
          (e.target.style.boxShadow = "0 0 25px #00ffaa, 0 0 50px #00ff88")
        }
      >
        {editing ? "Actualizar" : "Guardar"}
      </button>

      {/* Botón Eliminar solo si hay edición */}
      {editing && (
        <button
          type="button"
          onClick={() => onUpdate(editing.id, { ...editing, deleted: true })}
          style={{
            width: "40%",
            maxWidth: "200px",
            padding: "12px 0",
            fontSize: "1em",
            borderRadius: "12px",
            fontWeight: "bold",
            cursor: "pointer",
            background: "linear-gradient(45deg, #ff4d4d, #ff1a1a)",
            color: "#fff",
            boxShadow: "0 0 25px #ff4d4d, 0 0 50px #ff1a1a",
            transition: "0.3s"
          }}
          onMouseEnter={(e) =>
            (e.target.style.boxShadow = "0 0 50px #ff4d4d, 0 0 100px #ff1a1a")
          }
          onMouseLeave={(e) =>
            (e.target.style.boxShadow = "0 0 25px #ff4d4d, 0 0 50px #ff1a1a")
          }
        >
          Eliminar
        </button>
      )}
    </form>
  );
}
