import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function StudentForm({ onCreate, onUpdate, editing, onDelete }) {
  const [form, setForm] = useState({ nombre: "", carrera: "", nivel: "" });

  useEffect(() => {
    if (editing) setForm(editing);
    else setForm({ nombre: "", carrera: "", nivel: "" });
  }, [editing]);

  const submit = (e) => {
    e.preventDefault();
    if (editing) onUpdate(editing.id, form);
    else onCreate(form);
    setForm({ nombre: "", carrera: "", nivel: "" });
  };

  return (
    <motion.form
      initial={{ scale: 0.95, opacity: 0 }}
      animate={{ scale: 1, opacity: 1 }}
      transition={{ duration: 0.3 }}
      onSubmit={submit}
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "15px",
        alignItems: "center",
        marginBottom: 20
      }}
    >
      <input
        required
        placeholder="Nombre"
        value={form.nombre}
        onChange={(e) => setForm({ ...form, nombre: e.target.value })}
      />
      <input
        required
        placeholder="Carrera"
        value={form.carrera}
        onChange={(e) => setForm({ ...form, carrera: e.target.value })}
      />
      <input
        required
        placeholder="Nivel"
        value={form.nivel}
        onChange={(e) => setForm({ ...form, nivel: e.target.value })}
      />
      <button type="submit" style={{ background: "#00ff88", color: "#000" }}>
        {editing ? "Actualizar" : "Registrar"}
      </button>

      {editing && onDelete && (
        <button
          type="button"
          className="eliminar"
          onClick={() => onDelete(editing.id)}
          style={{
            background: "linear-gradient(45deg, #ff4d4d, #ff1a1a)",
            color: "#fff",
            boxShadow: "0 0 25px #ff4d4d, 0 0 50px #ff1a1a",
            padding: "12px 0",
            width: "50%",
            maxWidth: "200px",
            borderRadius: "12px",
            fontWeight: "bold",
            cursor: "pointer",
            transition: "0.3s"
          }}
          onMouseOver={(e) =>
            (e.currentTarget.style.boxShadow = "0 0 50px #ff4d4d, 0 0 100px #ff1a1a")
          }
          onMouseOut={(e) =>
            (e.currentTarget.style.boxShadow = "0 0 25px #ff4d4d, 0 0 50px #ff1a1a")
          }
        >
          Eliminar
        </button>
      )}
    </motion.form>
  );
}
