import React, { useState, useEffect } from "react";

export default function StudentForm({ onCreate, onUpdate, editing }) {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");

  useEffect(() => {
    if (editing) {
      setName(editing.name);
      setAge(editing.age);
    } else {
      setName("");
      setAge("");
    }
  }, [editing]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = { name, age };
    if (editing) {
      onUpdate(editing.id, data);
    } else {
      onCreate(data);
    }
    setName("");
    setAge("");
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "15px",
        alignItems: "center"
      }}
    >
      <input
        type="text"
        placeholder="Nombre del estudiante"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input
        type="number"
        placeholder="Edad"
        value={age}
        onChange={(e) => setAge(e.target.value)}
      />
      <button type="submit">{editing ? "Actualizar" : "Guardar"}</button>
      {editing && (
        <button
          type="button"
          className="eliminar"
          onClick={() => onUpdate(editing.id, { ...editing, deleted: true })}
        >
          Eliminar
        </button>
      )}
    </form>
  );
}
