import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";

export default function StudentForm({ onCreate, onUpdate, editing }) {
  const [form, setForm] = useState({ nombre: "", carrera: "", nivel: "" });

  useEffect(() => {
    if (editing) setForm(editing);
    else setForm({ nombre: "", carrera: "", nivel: "" });
  }, [editing]);

  const submit = (e) => {
    e.preventDefault();
    if (editing) onUpdate(editing.id, form);
    else onCreate(form);
    setForm({ nombre: "", carrera: "", nivel: "" });
  };

  return (
    <motion.form initial={{ scale: 0.95 }} animate={{ scale: 1 }} onSubmit={submit} style={{ marginBottom: 20 }}>
      <input required placeholder="Nombre" value={form.nombre} onChange={(e) => setForm({ ...form, nombre: e.target.value })} />
      <input required placeholder="Carrera" value={form.carrera} onChange={(e) => setForm({ ...form, carrera: e.target.value })} />
      <input required placeholder="Nivel" value={form.nivel} onChange={(e) => setForm({ ...form, nivel: e.target.value })} />
      <button type="submit">{editing ? "Actualizar" : "Registrar"}</button>
    </motion.form>
  );
}
