import React, { useState } from 'react';
import './App.css';

function App() {
  const [students, setStudents] = useState([]);
  const [name, setName] = useState('');
  const [age, setAge] = useState('');

  const addStudent = (e) => {
    e.preventDefault();
    if (name && age) {
      setStudents([...students, { name, age }]);
      setName('');
      setAge('');
    }
  };

  return (
    <div className="bienvenida">
      <h1>ðŸ“š Sistema de Estudiantes</h1>
      <p className="mensaje">Agrega y administra tus estudiantes de manera rÃ¡pida y elegante.</p>

      <div className="container">
        <h2>âž• Agregar Nuevo Estudiante</h2>
        <form onSubmit={addStudent}>
          <input
            type="text"
            placeholder="Nombre del estudiante"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="number"
            placeholder="Edad"
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />
          <button type="submit">Agregar</button>
        </form>

        {students.length > 0 && (
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Edad</th>
              </tr>
            </thead>
            <tbody>
              {students.map((s, i) => (
                <tr key={i}>
                  <td>{s.name}</td>
                  <td>{s.age}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default App;
